import styled from "styled-components";
import {ethers} from "ethers";
import React, {useEffect, useState} from "react";
import { Link } from 'react-router-dom'
import { useWeb3React } from "@web3-react/core";

import axios from "axios"
import {Container, Card, Button} from "react-bootstrap";
import {HiChevronDoubleUp, HiChevronDoubleDown} from "react-icons/hi"

const ActualPoolCard = styled.div`
    background: #1D1E20;
    box-shadow: 0px 2px 12px -8px rgba(25, 19, 38, 0.1), 0px 1px 1px rgba(25, 19, 38, 0.05);
    border-radius: 32px;
    display: flex;
    flex-direction: column;
    position: relative;
`
const Divider = styled.div`
  background-color: #fbfbfb;
  height: 1px;
  margin: 0 auto 32px;
  width: 100%;
`
const StyledCardContent = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  padding: 0;
`
const CardTitle = styled.div`
  font-weight: 600;
  font-size: 24px;
  line-height: 1.1;
  margin-bottom: 14px;
`

const StyledButton = styled.button`
  align-items: center;
  background: #fbdb37;
  border: 0;
  border-radius: 12px;
  color: #32cad7;
  cursor: pointer;
  display: flex;
  font-size: 100%;
  font-weight: 700;
  height: auto;
  justify-content: center;
  outline: none;
  padding-left: 5px;
  padding-right: 5px;
  pointer-events: ${(props) => (!props.disabled ? undefined : 'none')};
  width: 100%;
  border: 2px solid #33cbd7;
  font-size: 14px;
  padding: 4px;
`

const StyledLink = styled(Link)`
  align-items: center;
  color: inherit;
  display: flex;
  flex: 1;
  height: 56px;
  justify-content: center;
  margin: 0 2px;
  padding: 0 4px;
  text-decoration: none;
`

const StyledExternalLink = styled.a`
  align-items: center;
  color: inherit;
  display: flex;
  flex: 1;
  height: 56px;
  justify-content: center;
  margin: 0 4px;
  padding: 0 4px;
  text-decoration: none;
`

//footer expandable

const StyledFooter = styled.div`
  border-top: 1px solid #524B63;
  padding: 24px;
`

const StyledDetailsButton = styled.button`
  align-items: center;
  background-color: transparent;
  border: 0;
  color: #fbdb37;
  cursor: pointer;
  display: inline-flex;
  font-size: 16px;
  font-weight: 600;
  height: 32px;
  justify-content: center;
  outline: 0;
  padding: 0;
  &:hover {
    opacity: 0.9;
  }
  & > svg {
    margin-left: 4px;
  }
`

const Details = styled.div`
  margin-top: 24px;
`

const Row = styled.div`
  align-items: center;
  display: flex;
`

const FlexFull = styled.div`
  flex: 1;
`
const Label = styled.div`
  font-size: 14px;
`
const TokenLink = styled.a`
  font-size: 14px;
  text-decoration: none;
  color: #12aab5;
`
const CardFooter = ({
    projectLink,
    totalStaked,
    blocksRemaining,
    isFinished,
    blocksUntilStart,
    poolCategory,
  }) => {
    const [isOpen, setIsOpen] = useState(false)
    const Icon = isOpen ? HiChevronDoubleDown : HiChevronDoubleDown

    const handleClick = () => setIsOpen(!isOpen)

    return (
      <StyledFooter isFinished={isFinished}>
        <Row>
          <FlexFull>
            <Tag />
          </FlexFull>
          <StyledDetailsButton onClick={handleClick}>
            {isOpen ? 'Hide' : 'Details'} <Icon />
          </StyledDetailsButton>
        </Row>
        {isOpen && (
          <Details>
            <Row style={{ marginBottom: '4px' }}>
              <FlexFull>
                <Label>
                  <span role="img" aria-label="COB">
                    ðŸ¥ž{' '}
                  </span>
                  {'Total'}
                </Label>
              </FlexFull>
              <Balance fontSize="14px" isDisabled={isFinished} value={totalStaked} />
            </Row>

              <Row>
                <FlexFull>
                  <Label>{'End'}:</Label>
                </FlexFull>
                <Balance fontSize="14px" isDisabled={isFinished} value={blocksRemaining} decimals={0} />
              </Row>

            <TokenLink href={projectLink} target="_blank">
              {'View project site'}
            </TokenLink>
          </Details>
        )}
      </StyledFooter>
    )
  }


const PoolCard = () => {
    return (
        <>
    <Card>

      <div style={{ padding: '24px' }}>
        <CardTitle >
           {"COB"} {'Pool'}
        </CardTitle>
        <div style={{ marginBottom: '8px', display: 'flex', alignItems: 'center' }}>
          <div style={{ flex: 1 }}>
            <Image src={`/assets/images/CornLogo.png`} width={64} height={64} alt={"COB"} />
          </div>

            <HarvestButton/>

        </div>

        <Label  text={'COB earned'}/>
        <StyledCardActions>



                <Button>
                  {`Unstake COB`}
                </Button>
                <StyledActionSpacer />

                  <IconButton  >
                    <AddIcon color="background" />
                  </IconButton>



        </StyledCardActions>
        <StyledDetails>
          <div style={{ flex: 1 }}>APR: 43.34</div>

        </StyledDetails>
        <StyledDetails>
          <div style={{ flex: 1 }}>
            <span role="img" aria-label='COB'>
              ðŸ¥ž{' '}
            </span>
            Your Stake:
          </div>
          <Balance fontSize="14px" isDisabled={isFinished} value='324.34'/>
        </StyledDetails>
      </div>
      <CardFooter
        projectLink={"#"}
        totalStaked={"323.34"}
        blocksRemaining={"10340233"}
        isFinished={false}
        blocksUntilStart={"0"}
        poolCategory={""}
      />
    </Card>            
        </>

    )}

export default PoolCard
