import styled from "styled-components";
import {ethers} from "ethers";
import React, {useEffect, useState} from "react";
import { Link } from 'react-router-dom'
import { useWeb3React } from "@web3-react/core";
import axios from "axios"

//components
import {Container, Card, Button, Image} from "react-bootstrap";
import {HeaderButtonSecondary} from "../../vaults/index"
import {MultiplierBadge} from "./Badges"
import DepositModal from "./DepositModal"

//icons
import {HiChevronDoubleUp, HiChevronDoubleDown} from "react-icons/hi"
import {BsArrowUpRightSquare, BsCalculatorFill} from "react-icons/bs"
import {GoVerified} from "react-icons/go"
import {FaCircle, FaRegCircle, FaRegCheckCircle} from "react-icons/fa"
import {BiCoinStack} from "react-icons/bi"
import {RiCoinLine} from "react-icons/ri"


const ActualPoolCard = styled.div`
    background: #1D1E20;
    box-shadow: 0px 2px 12px -8px rgba(25, 19, 38, 0.1), 0px 1px 1px rgba(25, 19, 38, 0.05);
    border-radius: 32px;
    display: flex;
    flex-direction: column;
    position: relative;
`
const Divider = styled.div`
  background-color: #fbfbfb;
  height: 1px;
  margin: 0 auto 32px;
  width: 100%;
`
const StyledCardContent = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  padding: 0;
`
const CardTitle = styled.div`
  font-weight: 600;
  font-size: 24px;
  line-height: 1.1;
  margin-bottom: 14px;
`

const StyledButton = styled.button`
  align-items: center;
  background: #fbdb37;
  border: 0;
