import React, {useState, useEffect}from 'react'
import styled from "styled-components"
import NavigationBar from "../../components/NavigationBar"
import {Page} from "../../components/Page"
import {Vault} from "./components/CreateVault"

import {Card, Container, Button, Dropdown, Form} from "react-bootstrap"

const HeaderGridContainer = styled(Container)`
    margin-top: 25px;
    display: grid;
    grid-template-columns: auto auto;
    grid-template-rows: auto;
    justify-items: center;
    align-content: start;
    column-gap: 2px;
    row-gap: 20px;
    margin-bottom: 25px;

    @media (max-width: 768px) {
        flex-direction: column;
        grid-template-columns: auto;
        grid-template-rows: auto;
    }
`
const HeaderContentContainer = styled(Container)`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    align-content: center;
`

const BigHeading = styled.h1`
    font-size: 380%;
    font-weight: 800;
    color:  #fbdb37;
    margin-bottom: 24px;

`
const LittleHeading = styled.h2`
    font-size: 130%;
    font-weight: 600;
    color: #fbfbfb;

`
const HeaderImage = styled.img`
    height: 120%;
    width: auto;
    align-self: start;
`

const BackgroundImage = styled.div`
    aspect-ratio: 960/300;
    width: 100%;
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    background-image: url('/assets/images/homebackground.svg')
`

const HeaderButton = styled(Button)`
    border-radius: 15px;
    height: 50px;
    width: auto;
    background: #fbdb37;
    border-color: #fce984;
    border-width: 3px;
    color: #FFFFE0;
    font-size: 20px;
    font-weight: 600;
    margin-top: 20px;
    align-self: start;
    margin-right: 15px;

    &:hover {
        background: #fbdb37;
        border-color: #dfbb05;
        border-width: 3px;
        color: #dfbb05;
        font-size: 20px;
        font-weight: 800;
    }

    &:focus {
        background: #fbdb37;
        border-color: #dfbb05;
        border-width: 3px;
        color: #dfbb05;
        font-size: 20px;
        font-weight: 800;
    }
`
const HeaderButtonSecondary = styled(Button)`
    border-radius: 15px;
    height: 50px;
    width: auto;
    background: none;
    border-color: #fce984;
    border-width: 3px;
    color: #FFFFE0;
    font-size: 20px;
    font-weight: 600;
    margin-top: 20px;
    align-self: start;
    margin-right: 15px;

    &:hover {
        background: #fbdb37;
        border-color: #dfbb05;
        border-width: 3px;
        color: #dfbb05;
        font-size: 20px;
        font-weight: 800;
    }

    &:focus {
        background: #fbdb37;
        border-color: #dfbb05;
        border-width: 3px;
        color: #dfbb05;
        font-size: 20px;
        font-weight: 800;
    }
`
const StupidSpan = styled.span`
    align-self: start;
`
const BodyGridContainer = styled(Container)`
    margin-top: 25px;
    display: grid;
    grid-template-columns: auto auto;
    grid-template-rows: auto;
    justify-items: center;
    align-content: end;
    column-gap: 2px;
    row-gap: 20px;
    margin-bottom: 25px;

    @media (max-width: 768px) {
        flex-direction: column;
        grid-template-columns: auto;
        grid-template-rows: auto;
    }
`
const BodyContentContainer = styled(Container)`
    display: flex;
    flex-direction: column;
    align-items: start;
    justify-content: center;
    align-content: center;
`
const BodyBigHeading = styled(BigHeading)`
    font-weight: 600;
    font-size 190%;
    color: #fbdb37;
`
const BodyLittleHeading = styled(LittleHeading)`
    font-weight: 400;
    font-size: 120%;
    color: #fbfbfb;
`
const BodyContentCard = styled(Card)`
    align-self: center;
    height: auto;
    width: 90%;
    background-color: #1D1E20;
    background-radius: 8px;
    box-shadow: 0px 3px 15px rgba(0,0,0,0.2);
`
const BodyContentCardContainer = styled(Container)`
    width: 100%
    height: auto;
    padding: 8px;
    justify-content: center;
    align-items: center;
    align-content: center;
`
const BodyImageContainer = styled(Container)`
    align-self: center;
    width: auto;
    height: 80%;
`

const SubHeaderGridContainer = styled(BodyGridContainer)`
    grid-template-columns: auto auto auto;
    align-items: center;
    justify-items: center;
`

export const Home = () => {

    return (
        <>
        <Page>
            <HeaderGridContainer>
                <HeaderContentContainer>
                    <BigHeading>Beef-up with a Corn-fed portfolio</BigHeading>
                    <LittleHeading>Buy and Sell Algo-NFTs that can automate your on-chain trading strategies</LittleHeading>
                    <StupidSpan>
                    <HeaderButton>Mint Now</HeaderButton>
                    <HeaderButtonSecondary>Learn</HeaderButtonSecondary>
                    </StupidSpan>
                </HeaderContentContainer>
                <HeaderContentContainer>
                    <HeaderImage src={`/assets/images/CornLogo.png`}></HeaderImage>
                </HeaderContentContainer>
            </HeaderGridContainer>

            <SubHeaderGridContainer>
                <BodyContentContainer>
                    <h2>Hello</h2>
                    <h2>World</h2>
                    <h2>Shalom</h2>
                </BodyContentContainer>
                <BodyContentContainer>
                    <h2>Hello</h2>
                    <h2>World</h2>
                    <h2>Shalom</h2>
                </BodyContentContainer>
                <BodyContentContainer>
                    <h2>Hello</h2>
                    <h2>World</h2>
                    <h2>Shalom</h2>
                </BodyContentContainer>
            </SubHeaderGridContainer>

            <BodyGridContainer>
                <BodyContentContainer>
                    <BodyContentCard>
                        <BodyContentCardContainer>
                            <BodyBigHeading>Accumulator Distributor</BodyBigHeading>
                            <hr></hr>
                            <BodyLittleHeading>
                                Our smart contract is built on the principals of institutional algorithmic investing. Accumulate tokens at low prices and Distribute them at higher prices.
