import styled from "styled-components";
import {ethers} from "ethers";
import React, {useEffect, useState} from "react";
import { Link } from 'react-router-dom'
import { useWeb3React } from "@web3-react/core";
import axios from "axios"
import {Container, Card, Button, Image} from "react-bootstrap";
import {HiChevronDoubleUp, HiChevronDoubleDown} from "react-icons/hi"
import {BsArrowUpRightSquare} from "react-icons/bs"
import {GoVerified} from "react-icons/go"

import {MultiplierBadge} from "./Badges"

const ActualPoolCard = styled.div`
    background: #1D1E20;
    box-shadow: 0px 2px 12px -8px rgba(25, 19, 38, 0.1), 0px 1px 1px rgba(25, 19, 38, 0.05);
    border-radius: 32px;
    display: flex;
    flex-direction: column;
    position: relative;
`
const Divider = styled.div`
  background-color: #fbfbfb;
  height: 1px;
  margin: 0 auto 32px;
  width: 100%;
`
const StyledCardContent = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  padding: 0;
`
const CardTitle = styled.div`
  font-weight: 600;
  font-size: 24px;
  line-height: 1.1;
  margin-bottom: 14px;
`

const StyledButton = styled.button`
  align-items: center;
  background: #fbdb37;
  border: 0;
  border-radius: 12px;
  color: #32cad7;
  cursor: pointer;
  display: flex;
  font-size: 100%;
  font-weight: 700;
  height: auto;
  justify-content: center;
  outline: none;
  padding-left: 5px;
  padding-right: 5px;
  pointer-events: ${(props) => (!props.disabled ? undefined : 'none')};
  width: 100%;
  border: 2px solid #33cbd7;
  font-size: 14px;
  padding: 4px;
`

const StyledLink = styled(Link)`
  align-items: center;
  color: inherit;
  display: flex;
  flex: 1;
  height: 56px;
  justify-content: center;
  margin: 0 2px;
  padding: 0 4px;
  text-decoration: none;
`

const StyledExternalLink = styled.a`
  align-items: center;
  color: inherit;
  display: flex;
  flex: 1;
  height: 56px;
  justify-content: center;
  margin: 0 4px;
  padding: 0 4px;
  text-decoration: none;
`

//footer expandable

const StyledFooter = styled.div`
  border-top: 1px solid #524B63;
  padding: 24px;
`

const StyledDetailsButton = styled.button`
  align-items: center;
  background-color: transparent;
  border: 0;
  color: #fbdb37;
  cursor: pointer;
  display: inline-flex;
  font-size: 16px;
  font-weight: 600;
  height: 32px;
  justify-content: center;
  outline: 0;
  padding: 0;
  &:hover {
    opacity: 0.9;
  }
  & > svg {
    margin-left: 4px;
  }
`

const Details = styled.div`
  margin-top: 24px;
`

const Row = styled.div`
  align-items: center;
  display: flex;
`

const FlexFull = styled.div`
  flex: 1;
`
const Label = styled.div`
  font-size: 14px;
`
const TokenLink = styled.a`
  font-size: 14px;
  text-decoration: none;
  color: #12aab5;
`

const PoolFinishedSash = styled.div`
  background-position: top right;
  background-repeat: not-repeat;
  height: 135px;
  position: absolute;
  right: -24px;
  top: -24px;
  width: 135px;
`

const StyledCardActions = styled.div`
  display: flex;
  justify-content: center;
  margin: 16px 0;
  width: 100%;
  box-sizing: border-box;
`

const BalanceAndCompound = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-direction: row;
`

const StyledActionSpacer = styled.div`
  height: auto;
  width: 100%;
`

const StyledDetails = styled.div`
  display: flex;
  font-size: 14px;
`
const CardFooter = ({
    projectLink,
    totalStaked,
    blocksRemaining,
    isFinished,
    blocksUntilStart,
    poolCategory,
  }) => {
    const [isOpen, setIsOpen] = useState(false)
    const Icon = isOpen ? HiChevronDoubleUp : HiChevronDoubleDown

    const handleClick = () => setIsOpen(!isOpen)

    return (
      <StyledFooter isFinished={isFinished}>
        <Row>
          <FlexFull>
          </FlexFull>
          <StyledDetailsButton onClick={handleClick}>
            {isOpen ? 'Hide' : 'Details'} <Icon />
          </StyledDetailsButton>
        </Row>
        {isOpen && (
          <Details>
            <Row style={{ marginBottom: '4px' }}>
              <FlexFull>
                <Label>
                  <span role="img" aria-label="COB">
                  {'Total'}:
                  </span>

                </Label>
              </FlexFull>
              <p fontSize="14px" isDisabled={isFinished} value={totalStaked} />
            </Row>

              <Row>
                <FlexFull>
                  <Label>{'End'}:</Label>
                </FlexFull>
                <p fontSize="14px" isDisabled={isFinished} value={blocksRemaining} decimals={0} />
              </Row>

            <TokenLink href={projectLink} target="_blank">
              {'View project site'} <BsArrowUpRightSquare style={{marginLeft: '5px'}}/>
            </TokenLink>
          </Details>
        )}
      </StyledFooter>
    )
  }


const PoolCard = () => {
    return (
        <>
    <ActualPoolCard>

      <div style={{ padding: '24px' }}>

          <div style={{ display: 'flex', flexDirection: 'row', flexWrap: "wrap", alignItems: 'center'}}>
            <Image style={{ marginRight: "19px" }}src={`/assets/images/CornLogo.png`} width={64} height={64} alt={"COB"} />
            <CardTitle >

           <div style={{display: "flex", flexDirection: 'column', alignContent: "center", textAlign: "center", marginBottom: "12px"}}>

           {"COB"} {'Pool'}
           </div>

           <MultiplierBadge><GoVerified style={{marginRight: "4px"}}/>1% Fees</MultiplierBadge>
           <MultiplierBadge>100x</MultiplierBadge>
            </CardTitle>
          </div>


            <StyledDetails>
                <div style={{ display: "flex", flexDirection: "row", justifyContent: "space-between" }}>
                    <div>APR</div>
                    <div>125.53</div>
                </div>

            </StyledDetails>
            <StyledDetails>
                <div style={{ flex: 1 }}>
                    <span role="img" aria-label='COB'>

                    </span>

                </div>
                <p fontSize="14px"  value='324.34'/>
            </StyledDetails>

            <Label  text={'COB earned'}/>
            <StyledCardActions>



                <StyledButton>
                  {`Unstake COB`}
                </StyledButton>

                <StyledActionSpacer />





            </StyledCardActions>

      </div>


      <CardFooter
        projectLink={"#"}
        totalStaked={"323.34"}
        blocksRemaining={"10340233"}
        isFinished={false}
        blocksUntilStart={"0"}
        poolCategory={""}
        />
        </ActualPoolCard>            
        </>

)}

export default PoolCard
