import React, {useState, useEffect} from 'react'
import styled from "styled-components";
import {StyledDetailsButton, NFTLineBreak} from "../../pools/components/PoolCard";
import {MultiplierBadge} from "../../pools/components/Badges"
import {HiChevronDoubleUp, HiChevronDoubleDown} from "react-icons/hi"
import {FaTicketAlt} from "react-icons/fa"
import {Container, Card, Button} from "react-bootstrap";




const DetailsButtonWrapper = styled(Container)`
    display: flex;
    flex-direction: row;
    justify-content: flex-end;
    align-items: flex-end;
    margin-top: 12px;
`

const DetailsRow = styled(Container)`
    display: flex;
    flex-direction: row;
    justify-content: center;

`

const DetailsDropDownGrid = styled(Container)`
    margin-top: 12px;
    display: grid;
    grid-template-columns: auto auto;
    grid-template-rows: auto;

    place-items: center;
    column-gap: 2px;
    row-gap: 12px;
    margin-bottom: 25px;

    @media (max-width: 768px) {
        flex-direction: column;
        grid-template-columns: auto;
        grid-template-rows: auto;
    }

`

export const NFTFooter = () => {
    const [isOpen, setIsOpen] = useState(false)

    const handleDetailsOnClick = () => {
        setIsOpen( prev => !prev)
    }
    return (
        <>

        <DetailsButtonWrapper>
            <StyledDetailsButton style={{justifyContent: "flex-end"}} onClick={handleDetailsOnClick}>
                Details{isOpen ? <HiChevronDoubleUp/> : <HiChevronDoubleDown/>}
            </StyledDetailsButton>
        </DetailsButtonWrapper>

        { isOpen && 
            <DetailsDropDownGrid>
                <DetailsRow>
                    <FaTicketAlt style={{color: "#fbfbfb", fontSize: "200%"}}></FaTicketAlt>
                    <p style={{marginLeft: "3px"}}>x2</p>
                </DetailsRow>

            <MultiplierBadge>Performance Fee</MultiplierBadge>
            <MultiplierBadge>COB Rewards</MultiplierBadge>
            <p>Woah</p>
            <p>Woah</p>
            <p>Woah</p>
            </DetailsDropDownGrid>

        }

        </>
    )
}

export default NFTFooter
