import styled from "styled-components";
import axios from "axios"
import {ethers} from "ethers";
import React, {useEffect, useState} from "react";
import { useWeb3React } from "@web3-react/core";
//static confg
import { addresses } from "../../config/addresses";
import { nftURI } from "../../config/uri";
import {pools} from "../../config/pools";
import {nftABI, MasterChefABI} from "../../config/abis";
import {writeContract, userMint} from "../../utils/nft";
import {fetchUserPoolData, getPoolInfo, mapPendingToOriginalData} from "../../utils/fetchUserData";
//Components
import {Page} from "../../components/Page"
import {Container, Card, Button} from "react-bootstrap";

import PoolCard from "./components/PoolCard"
import {EthIcon, BitcoinIcon, DollarIcon} from "../vaults/components/CreateVault"

const MyVaultContainer = styled(Container)`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    gap: 12px;
    margin-top: 40px;
`
const MyVaultRow = styled(Container)`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 24px;
`

const MyVaultCard = styled(Card)`
    padding: 10px;
    border-radius: 8px;
    position: relative;
    z-index: 0;
    height: auto;
    width: 100%;
    background-color: #1D1E20;
    box-shadow: 0px 3px 15px rgba(0,0,0,0.2);
`
const MyVaultCardContainer = styled(Container)`
    display: flex;
    flex-direction: row;
    flex-wrap: no-wrap;
    align-items: baseline;
    align-content: space-around;
    justify-content: space-around;
    gap: 8px;
`
const PoolGrid = styled(Container)`
    margin-top: 25px;
    display: grid;
    grid-template-columns: auto auto;
    grid-template-rows: auto;
    justify-items: center;
    align-content: start;
    column-gap: 2px;
    row-gap: 20px;
    margin-bottom: 25px;

    @media (max-width: 768px) {
        flex-direction: column;
        grid-template-columns: auto;
        grid-template-rows: auto;
      }

`

const MintButton = styled(Button)`
    border-radius: 15px;
    height: 50px;
    width: 20%;
    background: #fbdb37;
    border-color: #fce984;
    border-width: 3px;
    color: #FFFFE0;
    font-size: 20px;
    font-weight: 600;
    margin-top: 20px;


    &:hover {
        background: #fbdb37;
        border-color: #dfbb05;
        border-width: 3px;
        color: #dfbb05;
        font-size: 20px;
        font-weight: 800;
    }

    &:focus {
        background: #fbdb37;
        border-color: #dfbb05;
        border-width: 3px;
        color: #dfbb05;
        font-size: 20px;
        font-weight: 800;
    }
`
const HeadingContainer = styled(Container)`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    margin-top: 15px;
`

const HeadingBackground = styled(Card)`
    height: auto;
    width: 100%;
    padding: 20px;
    background-color: #1D1E20;
    box-shadow: 0px 3px 15px rgba(0,0,0,0.2);
`

const BigHeading = styled.h1`
    font-size: 280%;
    font-weight: 800;
    color:  #fbdb37;

`
const LittleHeading = styled.h2`
    font-size: 200%;
    font-weight: 600;
    color: #fbfbfb;

`


const Pools = () => {

    const {active, account, library, connector} = useWeb3React();
    const [poolData, setPoolData] = useState(pools); //imported above
    const [masterChefContract, setMasterChefContract] = useState(null)


    useEffect( () => {
        if (active) {
          const masterChef = writeContract(
              active, 
              library.getSigner(), 
              account,
              addresses.masterChef,
              MasterChefABI,
              ).then(val => setMasterChefContract(val))

        } else {
            console.log("no masterchef")
          const noData = setMasterChefContract(null)
        }


      }, [active])

    useEffect( () => {
        if (masterChefContract && library && account) {

            const farmData = fetchUserPoolData(masterChefContract, library, account).then(val => val)
            const userFarmData = mapPendingToOriginalData(farmData, pools, masterChefContract, 4).then(val => val)

            setPoolData(userFarmData)
        } else {
            setPoolData(pools)
        }

    }, [masterChefContract, account, library])

    //if we do have pooldata then go ahead and populate a card for each pool
    if (masterChefContract !== null && active) {
        console.log(poolData)
        const mapPoolData =  poolData.map((pool, index) => (

            <PoolCard key={index} poolData={pool}/>

        ));

        return (
            <>
            <HeadingContainer>
                <HeadingBackground>
                    <BigHeading>Staking Pools</BigHeading>
                    <LittleHeading>Stake Assets to Earn COB</LittleHeading>
                </HeadingBackground>
            </HeadingContainer>
            <PoolGrid>
                {mapPoolData}
            </PoolGrid>



            </>
        )
    }

    //if we dont have pool data then return static dummy
    return (
        <>
        <Page>

            <HeadingContainer>
                <HeadingBackground>
                    <BigHeading>Staking Pools</BigHeading>
                    <LittleHeading>Stake Assets to Earn COB</LittleHeading>
                </HeadingBackground>
            </HeadingContainer>

            <PoolGrid>

                <PoolCard/>
                <PoolCard/>
                <PoolCard/>


            </PoolGrid>


        </Page>

        </>
    )
}

export default Pools
