import React from 'react'
import styled from "styled-components"
import NavigationBar from "../../components/NavigationBar"
import {Page} from "../../components/Page"

import {Card, Container, Button} from "react-bootstrap"

//icon
import { BsGearFill } from "react-icons/bs";

const VaultSectionWrapper = styled(Container)`
    margin-top: 50px;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;

`

const VaultCard = styled(Card)`
    padding: 5px;
    border-radius: 7% 7% 7% 7%;
    height: 400px;
    width: 480px;
    background-color: #1D1E20;
    box-shadow: 0px 3px 15px rgba(0,0,0,0.2);
`
const CardHeadingContainer = styled(Container)`
    display: flex;
    flex-direction: row;
    padding-top: 5px;
    margin: 8px;
    margin-bottom: 25px;
    align-items: center;
    height: auto;
    width: auto;
    justify-content: space-between;
`
const CardHeading = styled.h2`
    font-size: 26px;
    font-weight: 600;
`


const SettingsButton = styled(Button)`

    background-color: transparent !important;
    border: none;

    &:hover {
        background-color: transparent !important;
        border: none;
    }

    &:focus {
        background-color: transparent !important;
        border: none;
    }

`

const CardBodyContainer = styled(Container)`
    display: flex;
    flex-direction: column;
    padding: 5px;
    width: 98%;
    height: auto;
`

const TokenInput = styled.input`
    width: 100%;
    height: 100px !important;
    border: none;
    background: #292C2D;
    color: white;
    border-radius: 20px;
    padding: 15px;
    font-size: 30px;
    box-shadow: 0px 3px 15px rgba(0,0,0,0.2);
    text-align: end;
    direction: rtl;

`


const Vault = () => {
    return (
        <>
        <Page>
            <VaultSectionWrapper>

                <VaultCard>

                    <CardHeadingContainer>
                        <CardHeading>Create a Vault</CardHeading>
                        <SettingsButton><BsGearFill /></SettingsButton>
                    </CardHeadingContainer>
                    <TokenInput></TokenInput>



                </VaultCard>

            </VaultSectionWrapper>
        </Page>
        </>
    )
}

export default Vault
